mapper.select:
SELECT BOARD_ID, BOARD_CATE, BOARD_TITLE, CRET_USER, CRET_DATE, CRET_TIME, CHG_USER, CHG_DATE, CHG_TIME, CASE WHEN STR_TO_DATE(CONCAT(CRET_DATE,CRET_TIME),'%Y%m%d%H%i%s') BETWEEN DATE_ADD(now(), INTERVAL -1 DAY) AND NOW() THEN 1 ELSE 0 END AS AS_NEW, IFNULL((SELECT COUNT(BOARD_ID) AS VIEW_CNT FROM TBRD00102 B WHERE A.BOARD_ID = B.BOARD_ID GROUP BY BOARD_ID),0) AS VIEW_CNT, IFNULL((SELECT SUM(chu) as CHU_CNT FROM TBRD00103 C WHERE A.BOARD_ID = C.BOARD_ID GROUP BY BOARD_ID),0) AS CHU_CNT FROM TBRD00100 A WHERE 1=1 AND BOARD_CATE = #{boardCate}
AND BOARD_TITLE LIKE CONCAT('%',#{searchWord},'%')
AND BOARD_CNTNT LIKE CONCAT('%',#{searchWord},'%')
AND (BOARD_TITLE LIKE CONCAT('%',#{searchWord},'%') OR BOARD_CNTNT LIKE CONCAT('%',#{searchWord},'%'))
AND CRET_USER LIKE CONCAT('%',#{searchWord},'%')
ORDER BY BOARD_ID desc LIMIT #{start}, #{size}

mapper.update:
UPDATE TBRD00101 SET
	BOARD_ID = null
WHERE BOARD_ID = #{boardId}
