mapper.select:
SELECT BOARD_ID, BOARD_CATE, BOARD_TITLE, CRET_USER, CRET_DATE, CRET_TIME, CHG_USER, CHG_DATE, CHG_TIME, CASE WHEN STR_TO_DATE(CONCAT(CRET_DATE,CRET_TIME),'%Y%m%d%H%i%s') BETWEEN DATE_ADD(now(), INTERVAL -1 DAY) AND NOW() THEN 1 ELSE 0 END AS AS_NEW, IFNULL((SELECT COUNT(BOARD_ID) AS VIEW_CNT FROM TBRD00102 B WHERE A.BOARD_ID = B.BOARD_ID GROUP BY BOARD_ID),0) AS VIEW_CNT, IFNULL((SELECT SUM(LIKE_CHU) as LIKE_CHU_CNT FROM TBRD00103 C WHERE A.BOARD_ID = C.BOARD_ID GROUP BY BOARD_ID),0) AS LIKE_CHU_CNT, (SELECT USER_NM FROM TUSR00100 D WHERE A.CRET_USER = D.SEQ) AS CRET_USER_NM, (SELECT USER_NM FROM TUSR00100 E WHERE A.CHG_USER = E.SEQ) AS CHG_USER_NM FROM TBRD00100 A WHERE 1=1 AND BOARD_CATE = #{boardCate}
AND BOARD_TITLE LIKE CONCAT('%',#{searchWord},'%')
AND BOARD_CNTNT LIKE CONCAT('%',#{searchWord},'%')
AND (BOARD_TITLE LIKE CONCAT('%',#{searchWord},'%') OR BOARD_CNTNT LIKE CONCAT('%',#{searchWord},'%'))
AND CRET_USER LIKE CONCAT('%',#{searchWord},'%')
ORDER BY BOARD_ID desc LIMIT #{start}, #{size}

		SELECT 
			*
		FROM TBRD00100
		WHERE 1=1
          AND BOARD_ID = #{boardId}
		  AND BOARD_CATE = #{boardCate}

		  	AND CRET_USER = #{cretUser}

SELECT
	*
FROM TBRD00103
WHERE BOARD_ID = #{boardId}
  AND CRET_USER = #{cretUser}

mapper.update:
UPDATE TBRD00103 SET
	LIKE_CHU = #{likeChu},
	CHG_DATE = DATE_FORMAT(NOW(),'%Y%m%d'),
	CHG_TIME = DATE_FORMAT(NOW(),'%H%i%s')
WHERE BOARD_ID = #{boardId}

  AND CRET_USER = #{cretUser}

mapper.delete:
DELETE FROM TBRD00100 
WHERE BOARD_ID = #{boardId}

  AND CRET_USER = #{cretUser}

mapper.update:
UPDATE TBRD00101 SET
	BOARD_ID = null
WHERE BOARD_ID = #{boardId}
